jQuery(document).ready(function($) {
    

    $("#lightSlider").lightSlider({
        item: 1,
        slideMargin: 0,
        pager: false,
        enableDrag: false,
        responsive: [],
    });


    var slider = $('#class-detail-slider').lightSlider({
        gallery: true,
        item: 1,
        thumbItem: 6,
        slideMargin: 0,
        speed: 500,
        auto: false,
        loop: false,
        thumbMargin: 13,
        galleryMargin: 16,
        enableDrag: false,
        responsive : [
            {
                breakpoint:800,
                settings: {
                    thumbItem: 2
                  }
            }
        ],
        onSliderLoad: function() {
            $('#class-detail-slider').removeClass('cS-hidden');
        }
    });

    // init Isotope
});
